import React, { useState } from 'react';
import { Card, Badge, Button, Collapse } from 'react-bootstrap';
import ReactMarkdown from 'react-markdown';

export default function Job({ job, updateJob }) {
    const [open, setOpen] = useState(false)

    const handleAccept = () => {
        updateJob(
            {
                title: job.title,
                description: job.description,
                location: job.location,
                company_logo: job.company_logo,
                id: job.id,
                accepted: true
            }
        )
    }

    return (
        <Card className="mb-3">
            <Card.Body>
                <div className="d-flex justify-content-between">
                    <div>
                        <Card.Title>
                            {job.title} - <span className="text-muted font-weight-light">
                                {job.company}
                            </span>
                        </Card.Title>
                        {/* <Card.Subtitle className="text-muted mb-2">
                            {new Date(job.created_at).toLocaleDateString()}
                        </Card.Subtitle> */}
                        <Badge variant="secondary" className="mr-2">{job.type}</Badge>
                        <Badge variant="secondary">{job.location}</Badge>
                        <div style={{ wordBreak: 'break-all' }}>
                            <ReactMarkdown escapeHtml={false} source={job.how_to_apply} />
                        </div>
                    </div>
                    <img className="d-none d-md-block" height="50" alt={job.company} src={job.company_logo} />
                </div>
                <Card.Text>
                    <Button
                        onClick={() => setOpen(prevOpen => !prevOpen)}
                        variant="primary">{open ? 'Hide Details' : 'View Details'}</Button>
                </Card.Text>
                <Collapse in={open}>
                    <div className="mt-4">
                        <ReactMarkdown escapeHtml={false} source={job.description} />
                        <Button onClick={handleAccept}>Accept</Button>
                        <Button variant="danger">Decline</Button>
                    </div>
                </Collapse>
            </Card.Body>
        </Card>

    )
}
