import React from 'react';

const useAcceptedJobs = () => {

    const baseURL = 'http://localhost:8080/jobs';

    const getAllJobs = {
        getJobs() {
            return fetch(baseURL)
                .then(res => res.json());
        },

        addJob(job) {
            return fetch(baseURL, {
                method: 'POST',
                body: JSON.stringify(job),
                headers: {
                    'content-Type': 'application/json'
                }
            })
                .then(res => res.json());
        },

        deleteJob(id) {
            return fetch(baseURL + id, {
                method: 'DELETE'
            });
        }
    };


}

export default useAcceptedJobs;
