import 'bootstrap/dist/css/bootstrap.min.css';


function MyJobContainer() {

    const postJob = document.querySelector('.jobs-list');
    let output = '';

    const url = 'http://localhost:8080/jobs/myjobs';

    fetch(url)
        .then(res => res.json())
        .then(data => {
            data.forEach(post => {
                output += `
            <Card.Title>
            {job.title} - <span className="text-muted font-weight-light">
                {job.company}
            </span>
        </Card.Title>`;
            })
        });
        postJob.innerHTML = output;

};

export default MyJobContainer;
